// ========================
// تایپ‌های Response
// ========================

interface ActionResponse<T = unknown> {
  success: boolean
  data?: T
  error?: string
}

interface TestConnectionResult {
  connected: boolean
  message: string
  userCount?: number
}

interface LogsResult {
  logs: WpMigrationLog[]
  total: number
  page: number
  totalPages: number
}

// ========================
// شروع مهاجرت
// ========================

export async function startTaxonomyMigration(
  prevState: State,
  formData: FormData,
  options?: Partial<MigrationOptions>
): Promise<ActionResponse<MigrationRunResult>> {
  const values: { baseUrl: string; apiKey: string } =
    Object.fromEntries(formData)
  try {
    const migration = new TaxonomyMigration(values, {
      verbose: true,
      ...options,
    })

    const result = await migration.startMigration()

    return {
      success: true,
      data: result,
      values,
    }
  } catch (error) {
    return {
      success: false,
      message: error instanceof Error ? error.message : 'خطای ناشناخته',
      values,
    }
  }
}

// ========================
// Dry Run (تست بدون ذخیره)
// ========================

export async function dryRunUserMigration(
  options?: Partial<MigrationOptions>
): Promise<ActionResponse<MigrationRunResult>> {
  try {
    const migration = createUserMigration({
      verbose: true,
      dryRun: true,
      ...options,
    })

    const result = await migration.startMigration()

    return {
      success: true,
      data: result,
    }
  } catch (error) {
    return {
      success: false,
      message: error instanceof Error ? error.message : 'خطای ناشناخته',
    }
  }
}

// ========================
// Retry موارد Failed
// ========================

export async function retryFailedUsers(
  options?: Partial<MigrationOptions>
): Promise<ActionResponse<MigrationRunResult>> {
  try {
    const migration = createUserMigration({
      verbose: true,
      ...options,
    })

    const result = await migration.retryFailed()

    return {
      success: true,
      data: result,
    }
  } catch (error) {
    return {
      success: false,
      message: error instanceof Error ? error.message : 'خطای ناشناخته',
    }
  }
}

